@Slf4j  
public class Test10GuardedSuspention {  
    public static void main(String[] args) throws InterruptedException {  
        for (int i=0; i<3; i++) {  
            new People().start();  
        }  
        Thread.sleep(3000);  // 等的时间太短会导致迭代器部分并发错误  
        for (Integer id : MailBoxes.getIds()) {  
            log.debug("叫出邮递员{}", id);  
            new Postman(id, "||内容" + id).start();  
