    }  
}  

@Slf4j  
class People extends Thread {  
    @Override  
    public void run() {  
        // 收件人创建邮箱  
        GuardedObj go = MailBoxes.createGuardedObj();  
        log.debug("开始收信{}", go.getId());  
        // 超时等待收信  
        Object mail = go.get(5000);  
        if (mail!=null)  
            log.debug("收到了{},内容{}", go.getId(), mail);  
        else            log.debug("{}超时未收到", go.getId());  
